import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class BottomBar extends StatefulWidget {  @override  _BottomBarState createState() => _BottomBarState();}class _BottomBarState extends State<BottomBar> {  bool isExpandedView = false;//  bool isSelected = false;  int isSelectedIndex =0;  @override  Widget build(BuildContext context) {    return Column(      mainAxisAlignment: MainAxisAlignment.end,      children: [        Column(          mainAxisAlignment: MainAxisAlignment.end,          children: [            Container(              decoration: BoxDecoration(                color: Colors.white,                borderRadius:isExpandedView ? BorderRadius.only(                    topRight: Radius.circular(20),                    topLeft: Radius.circular(20)):null,                boxShadow: [                  BoxShadow(                    color: Colors.black,                    blurRadius: 5.0,                    spreadRadius: 0.0,                    offset:                    Offset(2.0, 2.0), // shadow direction: bottom right                  )                ],              ),              child: Row(                children: [                  buildBarKey(context, Icons.home, 'Home', 0),                  buildBarKey(context, Icons.access_time, 'Daily Updates', 1),                  buildBarKey(context, Icons.beach_access, 'Leaves', 2),                  InkWell(                    onTap: () {                      setState(() {                        isExpandedView = !isExpandedView;                      });                    },                    child: Container(                      height: 50,                      width: MediaQuery.of(context).size.width / 4,                      child: Stack(                        alignment: Alignment.center,                        children: [                          isExpandedView                              ? Container(                                  height: MediaQuery.of(context).size.height,                                  //width: MediaQuery.of(context).size.width / 6,                                  decoration: BoxDecoration(                                    shape: BoxShape.circle,                                    color: Colors.yellow,                                  ),                                )                              : Container(),                          Container(                            child: Column(                              mainAxisAlignment: MainAxisAlignment.center,                              crossAxisAlignment: CrossAxisAlignment.center,                              children: [                                Icon(                                  isExpandedView                                      ? Icons.more_horiz                                      : Icons.more_vert,                                  size: 10,                                  color: Colors.black,                                ),                                Text(                                  'More',                                  style: TextStyle(                                      fontSize: 10,                                      fontWeight: FontWeight.bold,                                      color: Colors.black),                                )                              ],                            ),                          ),                        ],                      ),                    ),                  ),                ],              ),            ),            isExpandedView                ? Container(                    decoration: BoxDecoration(                      color: Colors.white,                      /*border: Border(top: BorderSide(width: 1))*/                    ),                    child: Row(                      children: [                        buildBarKey(context, Icons.equalizer, 'Projects',3),                        buildBarKey(context, Icons.date_range, 'Calender',4),                        buildBarKey(context, Icons.person, 'Employees',5),                        buildBarKey(context, Icons.local_airport, 'Holiday',6),                      ],                    ),                  )                : Container(),          ],        ),      ],    );  }  Widget buildBarKey(BuildContext context, IconData icons, texts, int index) => InkWell(        onTap: () {          setState(() {            isSelectedIndex= index ;            //isSelected= ! isSelected;          });        },        child: Container(          height: 50,          width: MediaQuery.of(context).size.width / 4,        /*  decoration: BoxDecoration(borderRadius: BorderRadius.only(topLeft: Radius.circular(10),)),*/          child: Stack(            alignment: Alignment.bottomCenter,            children: [              Container(                child: Column(                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Icon(                      icons,                      size: 10,                      color: Colors.black,                    ),                    Text(                      texts,                      style: TextStyle(                          fontSize: 10,                          fontWeight: FontWeight.bold,                          color: Colors.black),                    )                  ],                ),              ),              isSelectedIndex == index                  ? Container(                      height: 4,                      width: MediaQuery.of(context).size.width / 6,                      decoration: BoxDecoration(                          color: Colors.yellow,                          borderRadius: BorderRadius.only(                              topRight: Radius.circular(40),                              topLeft: Radius.circular(40))),                    )                  : Container(),            ],          ),        ),      );}