import 'package:flutter/material.dart';import 'package:ui_challenge01/model/date_no.dart';import 'package:ui_challenge01/utils/styles.dart';class ListLeaveDate extends StatelessWidget {  List<Date>allDate=[    Date(day:'15' ,month:'May' ,year:'2020' ),    Date(day:'14' ,month:'May' ,year:'2020' ),    Date(day:'13' ,month:'May' ,year:'2020' ),    Date(day:'12' ,month:'May' ,year:'2020' ),    Date(day:'11' ,month:'May' ,year:'2020' ),    Date(day:'10' ,month:'May' ,year:'2020' ),  ];  @override  Widget build(BuildContext context) {    return Container(      height: MediaQuery.of(context).size.height*.17,      width: MediaQuery.of(context).size.width*.99,      child: ListView.builder(        scrollDirection: Axis.horizontal,        itemCount: allDate.length,        itemBuilder: (BuildContext context, index) => buildLeaveListUi(context, index),      ),    );  }  Widget buildLeaveListUi(BuildContext context, int index) {    return Container(     // color: Colors.black,      width: MediaQuery.of(context).size.width * .30,      child: Column(        children: [          Container(  height: MediaQuery.of(context).size.height * .15,             width: MediaQuery.of(context).size.width * .25,              padding: EdgeInsets.all(15),              decoration: BoxDecoration(                boxShadow: [BoxShadow(                  color: Styles.shadowColor,                  blurRadius: 9.0,                  spreadRadius: 0.0,                  offset: Offset(2.0, 2.0),                )],                border: Border.all(color: Colors.black),                shape: BoxShape.circle,color: Colors.grey,              ),              child: Column(crossAxisAlignment: CrossAxisAlignment.start,                mainAxisAlignment: MainAxisAlignment.spaceAround,                children: [                  Text(                    '${allDate[index].day}',                    style: TextStyle(                        fontSize: Styles.heavyTxt, fontWeight: FontWeight.bold),                  ),                  Column(crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Text(                        '${allDate[index].month} ${allDate[index].year}',                        style: TextStyle(                            fontWeight: FontWeight.bold,                            fontSize: Styles.smallTxt,                            ),                      ),                      Text(                        '(L)',                        style: TextStyle(                            fontSize: Styles.smallTxt,                            fontWeight: FontWeight.bold),                      )],                  ),                ],              )),          Center(              child: Text(                'APPROVED',                style: TextStyle(                    fontSize: Styles.smallTxt,                    color: Colors.lightGreen,                    fontWeight: FontWeight.bold),              ))        ],      ),    );  }}