import 'package:flutter/material.dart';import 'package:ui_challenge01/presentation/calender.dart';import 'package:ui_challenge01/presentation/custom_widget/bottom_bar.dart';import 'package:ui_challenge01/presentation/employees.dart';import 'package:ui_challenge01/presentation/holidays.dart';import 'package:ui_challenge01/presentation/home.dart';import 'package:ui_challenge01/presentation/leaves.dart';import 'package:ui_challenge01/presentation/projects.dart';import 'package:ui_challenge01/presentation/update.dart';class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int isSelectedIndex = 0;  @override  void initState() {    super.initState();  }  Widget build(BuildContext context) {    final tabOptions = [      Home(),      Update(),      Leaves(),      Projects(),      Calender(),      Employees(),      Holidays()    ];    return Scaffold(      resizeToAvoidBottomPadding: false,      //backgroundColor: Colors.green,     /* appBar: AppBar(        title: Text('iefgiwe'),      ),*/      body: Center(        child: tabOptions.elementAt(isSelectedIndex),      ),      bottomNavigationBar: BottomBar(),    );  }}