import 'package:flutter/material.dart';import 'package:ui_challenge01/presentation/calender.dart';import 'package:ui_challenge01/presentation/custom_widget/bottom_bar.dart';import 'package:ui_challenge01/presentation/employees.dart';import 'package:ui_challenge01/presentation/holidays.dart';import 'package:ui_challenge01/presentation/home.dart';import 'package:ui_challenge01/presentation/leaves.dart';import 'package:ui_challenge01/presentation/projects.dart';import 'package:ui_challenge01/presentation/update.dart';import 'home.dart';class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int isSelectedIndex = 0;  @override  void initState() {    super.initState();  }  Widget build(BuildContext context) {/*    final tabOptions = [      Home(),      Update(),      Leaves(),      Projects(),      Calender(),      Employees(),      Holidays()    ];*/    return Scaffold(      resizeToAvoidBottomPadding: false,      //backgroundColor: Colors.green,      /*appBar: AppBar(        title: Text('iefgiwe'),      ),*/      body: Center(        child: _getSelectedElement(),      ),      bottomNavigationBar: BottomBar(isSelectedIndex, _updateIndex),    );  }  _getSelectedElement() {    switch (isSelectedIndex) {      case 0:        return Home();      case 1:        return Update();      case 2:        return Leaves();      case 3:        return Projects();      case 4:        return Calender();      case 5:        return Employees();      case 6:        return Holidays();    }  }  _updateIndex(int index) {    setState(() {      isSelectedIndex = index;    });  }}