import 'package:flutter/material.dart';import 'package:ui_challenge01/presentation/custom_widget/list_leave_date.dart';import 'package:ui_challenge01/utils/styles.dart';class Leaves extends StatelessWidget {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Styles.backgroundColor,      body: SingleChildScrollView(          child: Container(        height: MediaQuery.of(context).size.height,        // color: Colors.lightGreen,        child: Column(          children: [            buildTopContainer(context),            BuildLeaveWindow(context),            buildLeaveRequest(context),          ],        ),      )),    );  }  Widget buildLeaveRequest(BuildContext context) {    return Container(      margin: EdgeInsets.all(20),      // color: Colors.blue,      width: MediaQuery.of(context).size.width,      height: MediaQuery.of(context).size.height / 6,      child: Column(        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Text(            'LEAVE REQUESTS',            style: TextStyle(                fontSize: Styles.midFont, fontWeight: FontWeight.bold),          ),          Row(            mainAxisAlignment: MainAxisAlignment.spaceBetween,            crossAxisAlignment: CrossAxisAlignment.center,            children: [              CircleAvatar(                backgroundColor: Styles.priColor,                radius: 19,                child: CircleAvatar(                  radius: 17,                  backgroundImage: NetworkImage(Styles.empAvtar),                ),              ),              SizedBox(                width: 5,              ),              Flexible(                  child: RichText(                text: TextSpan(                  /*  text: 'Default',*/                  style: TextStyle(fontSize: Styles.txtTagFont, wordSpacing: 5),                  /*defining default style is optional */                  children: <TextSpan>[                    TextSpan(                        text: 'Miku',                        style: TextStyle(                            color: Styles.secColor,                            fontWeight: FontWeight.bold)),                    TextSpan(                        text: ' and', style: TextStyle(color: Colors.grey)),                    TextSpan(                        text: ' 5 Others',                        style: TextStyle(                            color: Styles.secColor,                            fontWeight: FontWeight.bold)),                  ],                ),              )),              SizedBox(                width: 100,              ),              buildClipButton(),            ],          ),        ],      ),    );  }  Widget buildClipButton() {    return Container(        decoration: BoxDecoration(          borderRadius: BorderRadius.only(            topLeft: Radius.circular(30),            bottomLeft: Radius.circular(30),          ),          color: Styles.priColor,        ),        width: 35,        height: 45,        child: Icon(          Icons.arrow_back_ios,          size: Styles.smallTxt,        ));  }  Container buildTopContainer(BuildContext context) => Container(        padding: EdgeInsets.only(top: 50, bottom: 30),        child: Row(          mainAxisAlignment: MainAxisAlignment.center,          children: [            buildLeftStats(context),            Column(              children: [                SizedBox(                  height: 30,                ),                buildRightStats(context),              ],            ),          ],        ),      );  Widget buildLeftStats(BuildContext context) => Container(        margin: EdgeInsets.only(right: 10, bottom: 10),        padding: EdgeInsets.only(left: 30, right: 30, top: 30, bottom: 25),        width: 150,        height: MediaQuery.of(context).size.height * .15,        decoration: BoxDecoration(          shape: BoxShape.circle,          //borderRadius: BorderRadius.all(Radius.circular(5)),          color: Styles.priColor,          boxShadow: [            BoxShadow(              color: Colors.black,              blurRadius: 9.0,              spreadRadius: 0.0,              offset: Offset(2.0, 2.0), // shadow direction: bottom right            )          ],        ),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            Text(              'TOTAL LEAVES',              style: TextStyle(                  fontSize: Styles.txtTagFont,                  fontWeight: FontWeight.bold,                  color: Colors.white),            ),            SizedBox(              height: 5,            ),            Text(              '15',              style: TextStyle(                  fontSize: Styles.heavyTxt,                  fontWeight: FontWeight.bold,                  color: Styles.secColor),            ),            Text(              'Till April 2020',              style: TextStyle(                  fontSize: Styles.smallTxt,                  fontWeight: FontWeight.bold,                  color: Styles.secColor),            ),          ],        ),      );  Widget buildRightStats(BuildContext context) => Container(        margin: EdgeInsets.only(right: 10, bottom: 10),        padding: EdgeInsets.only(left: 30, right: 30, top: 30, bottom: 25),        width: 150,        height: MediaQuery.of(context).size.height * .15,        decoration: BoxDecoration(          shape: BoxShape.circle,          //borderRadius: BorderRadius.all(Radius.circular(5)),          color: Styles.secColor,          boxShadow: [            BoxShadow(              color: Styles.shadowColor,              blurRadius: 9.0,              spreadRadius: 0.0,              offset: Offset(2.0, 2.0), // shadow direction: bottom right            )          ],        ),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            Text(              'TOTAL LEAVES TAKEN',              style: TextStyle(                  fontSize: Styles.txtTagFont,                  fontWeight: FontWeight.bold,                  color: Colors.white),            ),            SizedBox(              height: 5,            ),            Text(              '0',              style: TextStyle(                  fontSize: Styles.heavyTxt,                  fontWeight: FontWeight.bold,                  color: Styles.priColor),            ),          ],        ),      );  Widget BuildLeaveWindow(BuildContext context) {    return Container(      height: MediaQuery.of(context).size.height * .35,      width: MediaQuery.of(context).size.width,      color: Colors.white,      padding: EdgeInsets.only(left: 10, right: 10),      child: Column(        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: [          Row(            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              Text(                'MY LEAVE RECORD',                style: TextStyle(                    fontWeight: FontWeight.bold,                    fontSize: Styles.midFont,                    color: Styles.secColor),              ),              Icon(                Icons.arrow_forward_ios,                size: Styles.smallTxt,              ),            ],          ),          ListLeaveDate(),        ],      ),    );  }}